package spinner

import (
	"fmt"
	"io"
	"os"
	"sync"
	"time"
)

type Spinner struct {
	mu         sync.Mutex
	frames     []string
	index      int
	active     bool
	stop       chan struct{}
	writer     io.Writer
	interval   func() time.Duration
	color      func() string
	hideCursor bool
}

type Option func(*Spinner)

func WithWriter(w io.Writer) Option {
	return func(s *Spinner) {
		s.writer = w
	}
}

func WithInterval(d time.Duration) Option {
	return func(s *Spinner) {
		s.interval = func() time.Duration {
			return d
		}
	}
}

func WithFrames(frames []string) Option {
	return func(s *Spinner) {
		s.frames = frames
	}
}

func WithIntervalFunc(f func() time.Duration) func(*Spinner) {
	return func(s *Spinner) {
		s.interval = f
	}
}

func WithColor(color string) func(*Spinner) {
	return func(s *Spinner) {
		s.color = func() string { return color }
	}
}

func WithColorFunc(f func() string) func(*Spinner) {
	return func(s *Spinner) {
		s.color = f
	}
}

func WithHideCursor(hide bool) func(*Spinner) {
	return func(s *Spinner) {
		s.hideCursor = hide
	}
}

var defaultFrames = []string{"‚†ã", "‚†ô", "‚†π", "‚†∏", "‚†º", "‚†¥", "‚†¶", "‚†ß", "‚†á", "‚†è"}

const (
	hideCursorSeq = "\033[?25l"
	showCursorSeq = "\033[?25h"
)

func New(opts ...Option) *Spinner {
	s := &Spinner{
		frames:     defaultFrames,
		stop:       make(chan struct{}),
		writer:     os.Stderr,
		interval:   func() time.Duration { return 60 * time.Millisecond },
		color:      func() string { return White },
		hideCursor: true,
	}

	for _, opt := range opts {
		opt(s)
	}

	return s
}

func (s *Spinner) Start() {
	s.mu.Lock()
	if s.active {
		s.mu.Unlock()
		return
	}
	s.active = true
	if s.hideCursor {
		fmt.Fprint(s.writer, hideCursorSeq)
	}
	s.mu.Unlock()

	go func() {
		for {
			select {
			case <-s.stop:
				return
			default:
				s.mu.Lock()
				fmt.Fprintf(s.writer, "\r%s%s%s", s.color(), s.frames[s.index], Reset)
				s.index = (s.index + 1) % len(s.frames)
				s.mu.Unlock()
				time.Sleep(s.interval())
			}
		}
	}()
}

func (s *Spinner) Stop() {
	s.mu.Lock()
	defer s.mu.Unlock()
	if s.active {
		s.active = false
		s.stop <- struct{}{}
		fmt.Fprint(s.writer, "\r \r")
		if s.hideCursor {
			fmt.Fprint(s.writer, showCursorSeq)
		}
	}
}

func Color256(n int) string {
	if n < 0 || n > 255 {
		return ""
	}
	return fmt.Sprintf("\033[38;5;%dm", n)
}

